mapper.select:
select memberId as memberId , email as email , password as password , name as name , role as role , passwordLock as passwordLock , regDate as regDate , status as status
from memberList
order by memberId desc limit #{pageStart},#{amount};

mapper.insert:
/*회원 가입*/

insert into memberlist (email, password, name) value (
    #{email}
    ,#{password}
    ,#{name}
    );

mapper.select:
select memberId as memberId , email as email , password as password , name as name , role as role , passwordLock as passwordLock , regDate as regDate , status as status
from memberList
order by memberId desc limit #{pageStart},#{amount};

mapper.update:
update memberList
set
    name = #{name},
    Password = #{password}

where memberId = #{memberId}
